import { Injectable } from '@angular/core';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import { CartItem } from '../models/cart-item.model';
import { ToastController } from '@ionic/angular';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { finalize } from 'rxjs/operators';

pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Injectable({
  providedIn: 'root',
})
export class PdfGeneratorService {
  downloadLink: string | null = null;

  constructor(private toastCtrl: ToastController, private storage: AngularFireStorage) {}

  async generateCartPdf(cartItems: CartItem[], totalAmount: number) {
    const documentDefinition = this.getDocumentDefinition(cartItems, totalAmount);

    try {
      pdfMake.createPdf(documentDefinition).getBlob(async (blob: Blob) => {
        const filePath = `cart_pdfs/Cart_${new Date().getTime()}.pdf`;
        const fileRef = this.storage.ref(filePath);
        const task = this.storage.upload(filePath, blob);

        task.snapshotChanges().pipe(
          finalize(async () => {
            const downloadURL = await fileRef.getDownloadURL().toPromise();
            console.log('File available at', downloadURL);
            this.presentToast('El PDF se gener√≥ correctamente. Por favor revisa la parte de abajo');
            this.downloadLink = downloadURL; // Asigna el enlace a la propiedad downloadLink
          })
        ).subscribe();
      });
    } catch (error) {
      console.error('Error generating PDF: ', error);
      this.presentToast('Error al generar el PDF.');
    }
  }

  private getDocumentDefinition(cartItems: CartItem[], totalAmount: number) {
    // Reemplaza el contenido de la cadena '...' con tu logo en base64
    const storeLogoBase64 = 'data:image/png;base64,'; // Tu logo en base64

    return {
      content: [
        {
          image: storeLogoBase64,
          width: 150,
          alignment: 'center',
          margin: [0, 0, 0, 20]
        },
        { text: 'Factura de Compra', style: 'header', alignment: 'center' },
        { text: 'Artesan√≠as Jim√©nez', style: 'subheader' },
        { text: 'Direcci√≥n: Antonio de Ulloa N23-49 y Mercadillo', style: 'subheader' },
        { text: 'Tel√©fono: +593 98 832 5130', style: 'subheader' },
        { text: 'Correo: info@artesaniasjimenez.com', style: 'subheader' },
        { text: `Orden de Compra No: ${this.generateOrderNumber()}`, style: 'orderNumber' },
        { text: 'Detalles de la factura', style: 'detailsHeader' },
        this.getItemsTable(cartItems),
        { text: 'Total: $' + totalAmount.toFixed(2), style: 'total' },
        { text: 'Gracias por su compra!', style: 'footer', alignment: 'center' }
      ],
      styles: {
        header: {
          fontSize: 22,
          bold: true,
          margin: [0, 0, 0, 10]
        },
        subheader: {
          fontSize: 12,
          margin: [0, 2, 0, 2]
        },
        orderNumber: {
          fontSize: 14,
          bold: true,
          margin: [0, 20, 0, 10]
        },
        detailsHeader: {
          fontSize: 16,
          bold: true,
          margin: [0, 10, 0, 10]
        },
        tableHeader: {
          bold: true,
          fontSize: 13,
          color: 'black',
          fillColor: '#f3f3f3'
        },
        tableBody: {
          fontSize: 12,
          margin: [0, 2, 0, 2]
        },
        total: {
          margin: [0, 20, 0, 0],
          fontSize: 16,
          bold: true,
          alignment: 'right'
        },
        footer: {
          margin: [0, 20, 0, 0],
          fontSize: 14,
          italics: true
        }
      },
      defaultStyle: {
        font: 'Roboto'
      }
    };
  }

  private getItemsTable(cartItems: CartItem[]) {
    return {
      table: {
        headerRows: 1,
        widths: ['*', 'auto', 'auto', 'auto'],
        body: [
          [
            { text: 'Producto', style: 'tableHeader' },
            { text: 'Cantidad', style: 'tableHeader' },
            { text: 'Precio', style: 'tableHeader' },
            { text: 'Subtotal', style: 'tableHeader' }
          ],
          ...cartItems.map(item => [item.name, item.quantity, `$${item.price.toFixed(2)}`, `$${(item.quantity * item.price).toFixed(2)}`])
        ]
      },
      layout: {
        fillColor: function (rowIndex: number, node: any, columnIndex: number) {
          return (rowIndex % 2 === 0) ? '#f9f9f9' : null;
        }
      }
    };
  }

  private generateOrderNumber(): string {
    const orderNumber = Math.floor(100000 + Math.random() * 900000).toString(); // Genera un n√∫mero de orden aleatorio de 6 d√≠gitos
    return orderNumber;
  }

  private async presentToast(message: string) {
    const toast = await this.toastCtrl.create({
      message,
      duration: 5000, // Incrementa la duraci√≥n para que el usuario tenga tiempo de copiar el enlace
      position: 'top',
    });
    toast.present();
  }
}
